import React from "react";
import "./Hero.css";
import Header from "./Header/Header";
import Heart from "../../assets/heart.png";
import hero_image from '../../assete/hero_image.png';
import hero_image_back from '../../assete/hero_image_back.png';
const Hero = () => {
    return ( <
        div className = "hero" >
        <
        div className = "left-h" >
        <
        Header / >
        <
        div className = "the-best-ad" > {
            " "
        } {
            /* Remove this empty div */
        } {
            " "
        } <
        span > The Best Fitness Club in Town < /span>{" "} < /
        div > {
            " "
        } {
            /* hero heading */
        } {
            " "
        } <
        div className = "hero-text" >
        <
        div >
        <
        span className = "stroke-text" > Shape < /span> <span>Your</span >
        <
        /div>{" "} <
        div >
        <
        span > Ideal Body < /span>{" "} < /
        div > {
            " "
        } <
        div >
        <
        span >
        in here we will help you shape and build your ideal body and live life to the fullest {
            " "
        } <
        /span>{" "} < /
        div > {
            " "
        } <
        /div>{" "} { / * Figures * / } { " " } <
        div className = "figures" >
        <
        div >
        <
        span > +140 < /span> <span>Expert coaches</span >
        <
        /div>{" "} <
        div >
        <
        span > +978 < /span> <span>Members Joined</span >
        <
        /div>{" "} <
        div >
        <
        span > +50 < /span> <span>Fitness programs</span >
        <
        /div>{" "} < /
        div > {
            " "
        } {
            /* hero buttons */
        } {
            " "
        } <
        div className = "hero-buttons" >
        <
        button className = "btn" > Get Started < /button>{" "} <
        button className = "btn" > Learn More < /button>{" "} < /
        div > {
            " "
        } <
        /div>{" "} <
        div className = "right-h" >
        <
        button className = "btn" > Join Now < /button>{" "} <
        div className = "heart-rate" >
        <
        img src = {
            Heart
        }
        alt = "" / >
        <
        span > Heart Rate < /span> <span>116 bpm</span >
        <
        /div>{" "} < /
        div > {
            " "
        } <
        div > {
            /* hero images */
        } <
        img src = {
            hero_image
        }
        alt = ""
        className = "hero-image" / >
        <
        img src = {
            hero_image_back
        }
        alt = ""
        className = "hero-image-back" / > < /
        div > < /
        div >
    );
};

export default Hero;
